mapper.update:
update member 
set M_NICKNAME = #{member.mNickname},
	M_NUMBER = #{member.mNumber},
	M_PW = #{member.mPw}
where M_ID = #{member.mId}

create event update_delete_event
on schedule every 1 minute starts now() + INTERVAL 1 SECOND
on completion not preserve
do
begin
    if not exists (select 1 from `member` where M_ID = #{member.mId} and
    M_DEL = 'y') then
        update `Member`
        set M_DEL = 'Y', M_DEL_TIME = now()
        where M_ID = #{member.mId};
    end if;

    delete from `member`
        where M_ID = #{member.mId}
        and M_DEL = 'y'
        and M_DEL_TIME < now() - INTERVAL 7 DAY;

    if row_count() > 0 then
        drop event if exists update_delete_event;
    end if;
end
